	function load_feature($enabled = false){
		if (!$this->oldFeatures) {
			// not needed at all for new feature areas
			//op_page_set_saved_settings($this->config, array());
		} else {
			if(!$this->enabled){
				parent::load_feature();
				return '';
			}
			$output = $this->load_style();
			parent::load_feature(true);
			echo $output;
		}
	}
	
	function typography($elements){
		$elements['font_elements']['landing_feature_arrows'] = array(
			'name' => __('Optin Box Arrow Text', 'optimizepress'),
			'help' => __('Title to appear above arrows above optin form', 'optimizepress')
		);
		return $elements;
	}
	
	function typography_selectors($elements){
		$elements['font_elements']['.featured-panel-style-1.featured-panel-4 .get-access-now h3, .featured-panel-style-1.featured-panel-5 .get-access-now h3'] = 'landing_feature_arrows';
		return $elements;
	}
	
	
	function color_scheme_selectors($schemes){
		unset($schemes['feature_area']);
		return $schemes;
	}
	
